import React, {Component} from 'react';
import {connect} from 'react-redux';
import {updatePost} from './../actions/postActions';
class UpdatePost extends Component{
    state = {
        data: {},
        post_title: '',
        post_desc: ''
    }
    componentDidMount(){
        this.setState({
            data: this.props.posts.filter( post => {
            return post.id === Number(this.props.match.params.id)
        })
        });
    }
    handleChange = (e) =>{
        e.preventDefault();
        this.setState({
            [e.target.id]: e.target.value
        })
    }
    handleSubmit = (e) =>{
        e.preventDefault();
        this.props.updatePost(this.state)
       // this.props.history.push('/posts')
    }    

    render(){
        const { data } = this.state;
        console.log(data[0]);
        return(
            <div>
                <h2 className="blue-text">Update Post</h2>
                <form onSubmit={this.handleSubmit}>
                <div className="row">
                    <div className="input-field col s6">
                    <input id="post_title" type="text" onChange={this.handleChange} className="validate" />
                    <label className="active" htmlFor="post_title">Post Title</label>
                    </div>
                    <div className="input-field col s6">
                    <textarea id="post_desc" className="materialize-textarea" onChange={this.handleChange}></textarea>
                    <label htmlFor="post_desc">Post Description</label>
                    </div>
                    <div className="input-field col s6">
                    <button className="btn">Update Post</button>
                    </div>
                </div>
                </form>
            </div>
        )
    }
}
const mapStoreToProps = (state) =>{
    return {
        posts: state.posts
    }
}
const mapDispatchToProps = (dispatch) => {

    return{
        updatePost: (data) => {
            dispatch(updatePost(data))
        }
    }
}
export default connect(mapStoreToProps ,mapDispatchToProps)(UpdatePost);
